task autonomous() {
	
}


void lineFollow(int fast, int slow) {

	if (vexRT[Btn5D]) {return;}

	if (SensorValue(rightIR) > 200 ) {
		setMotor(rightMotor, -fast);
	}

	if (SensorValue(rightIR) < 200) {
		setMotor(rightMotor, -slow);
	}

	if (SensorValue (leftIR) > 200) {
		setMotor(leftMotor, -fast);
	}

	if (SensorValue (leftIR) < 200) {
	 	setMotor(leftMotor, -slow);
	}
}


