/*---------------------------------------------------------------------------*/

task usercontrol() {
	enableCompetitionMode();

	while (true) {
		//Custom arcade control
		motor[leftMotor] = (vexRT[Ch3] + vexRT[Ch4]);
		motor[rightMotor] = (vexRT[Ch3] - vexRT[Ch4]);

		if (vexRT[Btn5U]) {
			for (int i = 0; i <1000000; i++) {
				if (i < 5000) {
					lineFollow(69, 20);
				} else if (5000 < y < 70000) {
					lineFollow(73, 29);
				} else if (70000 < y <120000) {
					lineFollow(69, 20);
				} else {
					lineFollow(73, 29);
				}
			}
		}
	}
}